import axios from 'axios';
import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'

const URI = 'http://localhost:8080/luciodb/'

const ShowEntry = () => {
    const [entry, setEntry] = useState([])

    useEffect(() => {
        getEntry();
    }, [])


    //Mostrar entradas
    const getEntry = async () => {
        const res = await axios.get(URI)
        setEntry(res.data)
    }


    //Borrar entrada
    const deleteEntry = async (id) => {
        await axios.delete(`${URI}${id}`)
        getEntry()
    }

    return (
        <div>
            {entry.map((user, index) => (
                <div key={index}>
                    <p>{user.nombre}</p>
                    <p>{user.comentario}</p>

                    <Link to={`/edit/${user._id} `} className="btn btn-primary">Editar</Link>
                    <button onClick={() => deleteEntry(entry._id)} className="btn btn-primary">Borrar</button>
                </div>
            ))}
        </div>
    )



}


export default ShowEntry;
