import axios from 'axios';
import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'
import './style.css'

const URI = 'http://localhost:8080/luciodb/'



console.log(axios.isCancel('something'));

const ShowProducts = () => {
    const [products, setProducts] = useState([])

    useEffect(() => {
        getProduct()
    }, [])


    //Mostrar entradas
    const getProduct = async () => {
        const res = await axios.get(URI)
        setProducts(res.data)
    }


    //Borrar entrada
    const deleteProduct = async (id) => {
        await axios.delete(`${URI}${id}`)
        getProduct()
    }

    return (
        <div className='container'>
            <div className='row'>
                <div className='col'>
                    <Link to="/create" className='btn btn-primary'>Crear</Link>
                    <table className='table'>
                        <thead className='table-primary'>
                            <tr>
                                <th className='td-color'>Nombre</th>
                                <th>Apellido</th>
                                <th>Edad</th>
                            </tr>
                        </thead>
                        <tbody>
                            {products.map((product, index) => (
                                <tr key={index}>
                                    <td className='td-color'> {product.nombre} </td>
                                    <td className='td-color'> {product.apellido} </td>
                                    <td className='td-color'> {product.edad} </td>
                                    <td>
                                        <Link to={`/edit/${product._id}`} >Edit</Link>
                                        <button onClick={() => deleteProduct(product._id)} className="btn btn-primary" >borrar</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    )

}


export default ShowProducts;
