import axios from 'axios';
import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'
import './style.css'

const URI = 'http://localhost:8080/luciodb/'



console.log(axios.isCancel('something'));
const ShowProducts = () => {
    const [products, setProducts] = useState([])

    useEffect(() => {
        getProduct()
    }, [])


    //Mostrar entradas
    const getProduct = async () => {
        const res = await axios.get(URI)
        setProducts(res.data)
    }


    //Borrar entrada
    const deleteProduct = async (id) => {
        await axios.delete(`${URI}${id}`)
        getProduct()
    }

    return (

        <div className="card card-compact w-96 bg-base-100 shadow-xl">
            {products.map((product, index) => {
                <div key={index}>
                    <figure><img src={product.foto} alt="Shoes" /></figure>
                    <div className="card-body" key={index}>
                        <h2 className="card-title">{product.nombre}</h2>
                        <p>{product.precio}</p>
                        <div className="card-actions justify-end">
                            <Link to={`/edit/${product._id}`} className="btn btn-active btn-primary">Edit</Link>
                            <button onClick={() => deleteProduct(product._id)} className="btn btn-active btn-primary" >Borrar</button>
                        </div>
                    </div>
                </div>
            })}
        </div>

    )
}


