import axios from 'axios';
import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'
import './style.css'

const URI = 'http://localhost:8080/luciodb/'



console.log(axios.isCancel('something'));
const ShowProducts = () => {
    const [products, setProducts] = useState([])

    useEffect(() => {
        getProduct()
    }, [])


    //Mostrar entradas
    const getProduct = async () => {
        const res = await axios.get(URI)
        setProducts(res.data)
    }


    //Borrar entrada
    const deleteProduct = async (id) => {
        await axios.delete(`${URI}${id}`)
        getProduct()
    }

    return (
        <div >
            <Link to="/create" className='btn btn-primary'>Crear</Link>
            <table className='table-auto'>
                <thead className='table-primary'>
                    <tr>
                        <th className='td-color'>Nombre</th>
                        <th>Foto</th>
                        <th>Precio</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map((product, index) => (
                        <tr key={index}>
                            <td className='td-color'> {product.nombre} </td>
                            <img src={product.foto} alt="not found" />
                            <td className='td-color'> {product.precio} </td>
                            <td>
                                <Link to={`/edit/${product._id}`} className="btn btn-active btn-primary">Edit</Link>
                                <button onClick={() => deleteProduct(product._id)} className="btn btn-active btn-primary" >borrar</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>

    )
}




export default ShowProducts;
