import axios from 'axios';
import { useState } from 'react'
import { useNavigate } from 'react-router-dom'



console.log(axios.isCancel('something'));


const URI = 'http://127.0.0.1:3001/db_lucio/'

// 127.0.0.1: 27017

const CreateProduct = () => {
    const [nombre, setNombre] = useState('')
    const [apellido, setApellido] = useState('')
    const [edad, setEdad] = useState('')
    const navigate = useNavigate()


    const store = async (e) => {
        e.preventDefault()
        await axios.post(URI, { nombre: nombre, apellido: apellido, edad: edad })
        navigate('/')
    }

    return (
        <div>
            <form onSubmit={store}>
                <div className='mb-3'>
                    <label className='form-label'>Nombre</label>
                    <input type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} className='form-control' />
                    <label className='form-label'>Apellido</label>
                    <input type="text" value={apellido} onChange={(e) => setApellido(e.target.value)} className='form-control' />
                    <label className='form-label'>Edad</label>
                    <input type="text" value={edad} onChange={(e) => setEdad(e.target.value)} className='form-control' />
                    {/* <label className='form-label'>Comentario</label>
                    <textarea type="text" value={comentario} onChange={(e) => setComentario(e.target.value)} className='form-control' /> */}
                </div>
                <button type='submit' className='btn btn-primary'>Guardar</button>

            </form>
        </div>
    )
}

export default CreateProduct;
