import axios from 'axios';
import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'
import './style.css'

const URI = 'http://localhost:8080/luciodb/'



console.log(axios.isCancel('something'));
const ShowProducts = () => {
    const [products, setProducts] = useState([])

    useEffect(() => {
        getProduct()
    }, [])


    //Mostrar entradas
    const getProduct = async () => {
        const res = await axios.get(URI)
        setProducts(res.data)
    }


    //Borrar entrada
    const deleteProduct = async (id) => {
        await axios.delete(`${URI}${id}`)
        getProduct()
    }

    return (
        <>
            <ul className="menu bg-base-100 w-56 p-2 rounded-box">
                <li><a>Item 1</a></li>
                <li><a>Item 2</a></li>
                <li><a>Item 3</a></li>
            </ul>

            <div>
                <Link to="/create" className='btn btn-primary'>Crear</Link>
                <table className='table-auto'>
                    <thead className='table-primary'>
                        <tr>
                            <th className='td-color'>Nombre</th>
                            <th>Foto</th>
                            <th>Precio</th>
                        </tr>
                    </thead>
                    <tbody>
                        {products.map((product, index) => (
                            <tr key={index}>
                                <td className='td-color'> {product.nombre} </td>
                                <img src={product.foto} alt="not found" />
                                <td className='td-color'> {product.precio} </td>
                                <td>
                                    <Link to={`/edit/${product._id}`} className="boton" >Edit</Link>
                                    <button onClick={() => deleteProduct(product._id)} className="boton" >borrar</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </>
    )
}


export default ShowProducts;
